SVGElement.prototype.hasClass=function(a){return new RegExp("(\\s|^)"+a+"(\\s|$)").test(this.getAttribute("class"));};SVGElement.prototype.addClass=function(a){if(!this.hasClass(a)){this.setAttribute("class",this.getAttribute("class")+" "+a);
}};SVGElement.prototype.removeClass=function(a){var b=this.getAttribute("class").replace(new RegExp("(\\s|^)"+a+"(\\s|$)","g"),"$2");if(this.hasClass(a)){this.setAttribute("class",b);
}};SVGElement.prototype.toggleClass=function(a){if(this.hasClass(a)){this.removeClass(a);}else{this.addClass(a);}};